INSERT INTO `jh_posts` VALUES (114, 1, '2018-10-31 23:11:48', '2018-10-31 15:11:48', '北京金汇诚达科技有限公司注册资金5000万元人民币，成立于2016年8月，目前员工人数58人，经营范围：电子计算机软件、硬件及外部设备的技术开发、技术咨询、转让与服务等，其自主研发的社区智能无人碾米机，采用智能+现碾模式，将古法现碾米的形式与智能远程操作终端结合，以“产地控制+标准溯源+糙米流通+现碾现售”的方式进行商业模式创新，拥有黑龙江省范围内遴选优质、稀缺的原产地资源，通过生态科学种植，专业化服务、种植过程可追溯、现吃现碾等方法为追求高品质食品，解决食品安全问题。', '企业介绍', '', 'publish', 'closed', 'closed', '', '%e4%bc%81%e4%b8%9a%e4%bb%8b%e7%bb%8d', '', '', '2018-10-31 23:11:48', '2018-10-31 15:11:48', '', 25, 'http://www.jinhuichengda.com/?page_id=114', 0, 'page', '', 0);
